import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import LicencasMapModal from './LicencasMapModal';
import { fetchLicencas, createLicenca, updateLicenca, excluirLicenca } from '../../services/LicencasService';

const blueIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const grayIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-grey.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const getIconForLicenca = (validade, status) => {
  if (status === 'Em Análise') {
    return blueIcon;
  }

  const hoje = new Date();
  const dataValidade = new Date(validade);
  const diferencaTempo = dataValidade.getTime() - hoje.getTime();
  const diasParaVencer = Math.ceil(diferencaTempo / (1000 * 3600 * 24));

  if (diasParaVencer < 0) {
    return redIcon;
  } else if (diasParaVencer <= 120) {
    return yellowIcon;
  } else {
    return greenIcon;
  }
};

const Dashboard = () => {
  const [licencas, setLicencas] = useState([]);
  const [novaLicenca, setNovaLicenca] = useState(null);
  const [modoCriacao, setModoCriacao] = useState(false);
  const [formData, setFormData] = useState({
    razao_social: '',
    atividade: '',
    endereco: '',
    numero_licenca: '',
    numero_processo: '', // Adicionado
    tipo_licenca: '',
    data_emissao: '',
    validade: '',
    status: 'Ativo'
  });
  const [licencaEdicao, setLicencaEdicao] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);

  useEffect(() => {
    const getLicencas = async () => {
      const data = await fetchLicencas();
      setLicencas(data);
    };
    getLicencas();
  }, []);

  const handleEditClick = (licenca) => {
    setLicencaEdicao(licenca);
    setFormData({ ...licenca });
    setIsModalVisible(true);
  };

  const handleCancel = () => {
    setModoCriacao(false);
    setIsModalVisible(false);
    setFormData({
      razao_social: '',
      atividade: '',
      endereco: '',
      numero_licenca: '',
      numero_processo: '',
      tipo_licenca: '',
      data_emissao: '',
      validade: '',
      status: 'Ativo'
    });
    setLicencaEdicao(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (licencaEdicao) {
      await updateLicenca(licencaEdicao.id, formData);
    } else {
      await createLicenca(formData);
    }
    setIsModalVisible(false);
    setModoCriacao(false);
    const data = await fetchLicencas();
    setLicencas(data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
      <div style={{ flexGrow: 1 }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '100%' }}>
          <h2>Mapa de Licenças Ambientais - DIMEA</h2>
          {!modoCriacao ? (
            <Button onClick={() => setModoCriacao(true)} style={{ marginBottom: '10px' }}>
              <span>+</span> Adicionar Licença
            </Button>
          ) : (
            <Button onClick={handleCancel} style={{ marginBottom: '10px' }}>
              Cancelar
            </Button>
          )}
        </div>
        <MapContainer center={[campoGrandeLat, campoGrandeLng]} zoom={13} style={{ height: '100vh', width: '100%' }} zoomControl={false}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <BasemapControl />
          <MapaEventos />
          {licencas.map((licenca) => (
            <Marker
              key={licenca.id}
              position={[licenca.latitude, licenca.longitude]}
              icon={getIconForLicenca(licenca.validade, licenca.status)}
            >
              <Popup>
                <strong>{licenca.razao_social}</strong>
                <p>Atividade: {licenca.atividade}</p>
                <p>Endereço: {licenca.endereco}</p>
                <p>Núm. da Licença: {licenca.numero_licenca}</p>
                <p>Núm. do Processo: {licenca.numero_processo || 'Não informado'}</p>
                <p>Status: {licenca.status}</p>
                <p>Validade: {licenca.validade}</p>
                <p>Id: {licenca.id}</p>
                <button onClick={() => handleEditClick(licenca)} style={{ padding: '10px 20px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', fontSize: '16px', cursor: 'pointer', marginTop: '10px' }}>
                  Editar
                </button>
                <button onClick={() => excluirLicenca(licenca.id)} style={{ padding: '10px 20px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', fontSize: '16px', cursor: 'pointer', marginTop: '10px', marginLeft: '10px' }}>
                  Excluir
                </button>
              </Popup>
            </Marker>
          ))}
          {(novaLicenca || licencaEdicao) && (
            <Marker
              position={[novaLicenca ? novaLicenca.latitude : licencaEdicao.latitude, novaLicenca ? novaLicenca.longitude : licencaEdicao.longitude]}
              icon={getIconForLicenca(formData.validade, formData.status, formData.numero_processo)}
            >
              <Popup
                autoPanPadding={[50, 50]} // Ajusta o padding pra evitar sobreposição
                maxWidth={320} // Limita a largura do popup
                onOpen={() => {
                  // Opcional: adiciona um listener pra propagar o scroll pro mapa
                  document.querySelector('.leaflet-popup-content-wrapper').addEventListener('wheel', (e) => {
                    e.stopPropagation();
                    document.querySelector('.leaflet-container').dispatchEvent(new Event('wheel'));
                  });
                }}
              >
                <LicencasMapModal
                  formData={formData}
                  onChange={handleChange}
                  onSubmit={handleSubmit}
                  onCancel={handleCancel}
                  isEditMode={!!licencaEdicao}
                />
              </Popup>
            </Marker>
          )}
        </MapContainer>
        {isModalVisible && (
          <LicencasMapModal
            formData={formData}
            onChange={handleChange}
            onSubmit={handleSubmit}
            onCancel={handleCancel}
            isEditMode={!!licencaEdicao}
          />
        )}
        <div
          style={{
            position: 'absolute',
            top: '200px',
            right: '20px',
            backgroundColor: 'white',
            padding: '10px',
            borderRadius: '5px',
            boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
            zIndex: 1000,
          }}
        >
          <h4 style={{ margin: '0 0 10px 0', fontSize: '16px' }}>Legenda</h4>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
            <img src="https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-grey.png" alt="Ícone Cinza" style={{ width: '20px', height: '30px', marginRight: '5px' }} />
            <span>Processo Aberto</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
            <img src="https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-green.png" alt="Ícone Verde" style={{ width: '20px', height: '30px', marginRight: '5px' }} />
            <span>Licença Válida</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
            <img src="https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png" alt="Ícone Azul" style={{ width: '20px', height: '30px', marginRight: '5px' }} />
            <span>Licença em Análise</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
            <img src="https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-yellow.png" alt="Ícone Amarelo" style={{ width: '20px', height: '30px', marginRight: '5px' }} />
            <span>Licença Prestes a Vencer</span>
          </div>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <img src="https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png" alt="Ícone Vermelho" style={{ width: '20px', height: '30px', marginRight: '5px' }} />
            <span>Licença Vencida</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
